import React, { useState } from "react";
import "./App.css";
import Aux from "./Auxiliary/Auxiliary";

function App() {
  const[fullName, setFullName] = useState( {
   fname: ' ', 
   lname : ' '
  });

  const inputEvent = (event) => {
    const  {value , name} = event.target;

  }
  setFullName( (preValue) => {
    if (name === "fname"){
      return {
        fname: value, 
        lname: preValue.lname
      };
    }
    else if (name === "lname"){
      return {
        fname: preValue.fname,
        lname : value
      }
    }
  })

  submitted



  return (
    <Aux>
    <h1>Hello {fullName.fname} {fullName.lname} </h1>
    <form onSubmit = {submitted}>
    <input type="text" placeholder = "Enter Your Name"  name = 'fname' onChange = {inputEvent} value = {fullName.name} /><br />
    <input type="text"  placeholder = "Enter your last name"  name = 'lname' onChange = {inputEvent} value = {fullName.lname} />< br/>
    <button >Submit</button>
    </form>

    </Aux>
  );
}
export default App;
