import React, { useState } from "react";
import "./App.css";
import Aux from "./Auxiliary/Auxiliary";

function App() {
  const [name, func] = useState("");
  const[eventget, getvalue] = useState();
  const[eventget2, getvalue2] = useState();

  const setvalue = (event) => {
    getvalue(event.target.value);
    getvalue2(event.target.value);
  }

const submitted = () => {
  func(eventget);
  func(eventget2);
}
  return (
    <Aux>
    <h1>Hello {name}</h1>
    <form onSubmit = {submitted}>
    <input type="text" placeholder = "Enter Your Name" onChange = {setvalue} value = {eventget} /><br />
    <input type="text"  placeholder = "Enter your last name" onChange = {setvalue} value = {eventget2} />< br/>
    <button >Submit</button>
    </form>

    </Aux>
  );
}
export default App;
